import{a as h}from"./chunk-6JTKFIGZ.js";import{a as p}from"./chunk-6FMLIRIY.js";import{Jb as y,L as t,Ma as c,Na as r,ha as d,ra as g,rb as o}from"./chunk-ZQZS5QUD.js";import{a,b as i,g as v}from"./chunk-P2VZOJAX.js";var b=v(y());var x={title:"Extension Onboarding",mdFile:"./index.md",category:h,order:4},s=x;var u=[];var j={},m=j;var w=`<h1 id="extension-onboarding" class="ngde">Extension Onboarding<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#extension-onboarding"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h1><p class="ngde">Welcome to the team! We're excited to have you on board as a new Browser Extension Developer. This document will guide you through the onboarding process and help you set up your local development environment using the WXT framework, ReactJS, and TypeScript.</p><h4 id="pre-onboarding-checklist" class="ngde">Pre-Onboarding Checklist<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#pre-onboarding-checklist"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><p class="ngde">Before you start, ensure you have the following:</p><ul class="ngde"><li class="ngde">A computer with administrative access.</li><li class="ngde">Internet connection.</li><li class="ngde">Access to the team's repository on GitHub/Bitbucket/Azure DevOps (request access if not already provided).</li><li class="ngde">An active developer account for the relevant browser (e.g., Chrome Web Store, Mozilla Add-ons, etc.).</li></ul><h4 id="step-1-install-required-software" class="ngde">Step 1: Install Required Software<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#step-1-install-required-software"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ol class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Node.js and npm</strong></p><ul class="ngde"><li class="ngde">Download and install the latest LTS version of Node.js from <a href="https://nodejs.org/" class="ngde">nodejs.org</a>.</li><li class="ngde">Verify the installation by running the following commands in your terminal:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">node -v
</span><span class="line ngde">npm -v
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Git</strong></p><ul class="ngde"><li class="ngde">Download and install Git from <a href="https://git-scm.com/" class="ngde">git-scm.com</a>.</li><li class="ngde">Configure Git with your user information:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">git config --global user.name <span class="hljs-string ngde">"Your Name"</span>
</span><span class="line ngde">git config --global user.email <span class="hljs-string ngde">"your.email@example.com"</span>
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">WXT CLI</strong></p><ul class="ngde"><li class="ngde">Install the WXT CLI globally:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm install -g @wxt/cli
</span></code></pre></li><li class="ngde">Verify the installation:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">wxt --version
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Visual Studio Code (VS Code)</strong></p><ul class="ngde"><li class="ngde">Download and install VS Code from <a href="https://code.visualstudio.com/" class="ngde">code.visualstudio.com</a>.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Browser Developer Tools</strong></p><ul class="ngde"><li class="ngde">Ensure you have the Chrome and/or Firefox Developer Tools installed, which allow you to test and debug extensions:<ul class="ngde"><li class="ngde"><a href="https://chrome.google.com/webstore/developer/dashboard" class="ngde">Chrome Extensions Developer Dashboard</a></li><li class="ngde"><a href="https://addons.mozilla.org/en-US/developers/" class="ngde">Firefox Developer Hub</a></li></ul></li></ul></li></ol><h4 id="step-2-set-up-your-development-environment" class="ngde">Step 2: Set Up Your Development Environment<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#step-2-set-up-your-development-environment"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ol class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Clone the Repository</strong>:</p><ul class="ngde"><li class="ngde">Clone the project repository using Git:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">git <span class="hljs-built_in ngde">clone</span> https://github.com/organization/project-browser-extension.git
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Install Dependencies</strong>:</p><ul class="ngde"><li class="ngde">Navigate to the project folder and install dependencies using npm:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm install
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Environment Variables</strong>:</p><ul class="ngde"><li class="ngde">Set up environment variables by copying the <code class="ngde">.env.example</code> file to <code class="ngde">.env</code> and configuring the necessary variables for local development.</li><li class="ngde">Example:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">API_BASE_URL=<span class="hljs-string ngde">"https://api.example.com"</span>
</span><span class="line ngde">BROWSER=<span class="hljs-string ngde">"chrome"</span>
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Running the Extension in Development Mode</strong>:</p><ul class="ngde"><li class="ngde">Use WXT to build and load the extension into your browser\u2019s developer environment:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm run start:chrome
</span></code></pre></li><li class="ngde">For Firefox or other browsers, adjust the command accordingly:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm run start:firefox
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Building the Extension for Production</strong>:</p><ul class="ngde"><li class="ngde">When you're ready to build the extension for production, use the following command:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm run build
</span></code></pre></li></ul></li></ol><h4 id="step-3-set-up-vs-code" class="ngde">Step 3: Set Up VS Code<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#step-3-set-up-vs-code"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ol class="ngde"><li class="ngde"><strong class="ngde">Install Required Extensions</strong><ul class="ngde"><li class="ngde">Launch VS Code and install the following extensions:<ul class="ngde"><li class="ngde"><strong class="ngde">ESLint</strong>: <code class="ngde">ESLint</code></li><li class="ngde"><strong class="ngde">Prettier - Code Formatter</strong>: <code class="ngde">Prettier</code></li><li class="ngde"><strong class="ngde">ReactJS Snippets</strong>: <code class="ngde">ES7+ React/Redux/React-Native snippets</code></li><li class="ngde"><strong class="ngde">TypeScript Hero</strong>: <code class="ngde">TypeScript Hero</code></li><li class="ngde"><strong class="ngde">Debugger for Chrome</strong>: <code class="ngde">Debugger for Chrome</code></li><li class="ngde"><strong class="ngde">Jest</strong>: <code class="ngde">Jest</code> (if testing with Jest)</li><li class="ngde"><strong class="ngde">GitLens</strong>: <code class="ngde">GitLens \u2014 Git supercharged</code></li></ul></li></ul></li></ol><h4 id="step-4-running-tests" class="ngde">Step 4: Running Tests<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#step-4-running-tests"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ol class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Unit Tests</strong>:</p><ul class="ngde"><li class="ngde">Run unit tests using the following command:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm run <span class="hljs-built_in ngde">test</span>
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Integration Tests</strong>:</p><ul class="ngde"><li class="ngde">If there are integration tests, run them using:<pre class="ngde hljs"><code class="hljs language-bash code-lines ngde" lang="bash" name="" icon="" highlightedlines="[]"><span class="line ngde">npm run <span class="hljs-built_in ngde">test</span>:integration
</span></code></pre></li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Debugging</strong>:</p><ul class="ngde"><li class="ngde">Use the VS Code Chrome Debugger extension to debug the browser extension directly within the IDE.</li></ul></li></ol><h4 id="step-5-additional-resources-and-support" class="ngde">Step 5: Additional Resources and Support<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#step-5-additional-resources-and-support"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ol class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Documentation</strong></p><ul class="ngde"><li class="ngde">Familiarize yourself with the project\u2019s documentation available in the repository\u2019s <a href="https://rp@dev.azure.com/rp/project-browser-extension" class="ngde">README.md file</a> or the project wiki.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Team Communication</strong></p><ul class="ngde"><li class="ngde">Join the team\u2019s communication channels (Slack, Google Chat, etc.) and introduce yourself.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Mentorship and Support</strong></p><ul class="ngde"><li class="ngde">You will be paired with a mentor who can help guide you through the project architecture, development practices, and tools used by the team.</li></ul></li></ol><h4 id="conclusion" class="ngde">Conclusion<a title="Link to heading" class="ng-doc-header-link ngde" href="/onboarding-process/extension-onboarding#conclusion"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><p class="ngde">You are now set up and ready to start developing your browser extension. We look forward to your contributions and collaboration. Welcome to the team!</p><p class="ngde">If you have any questions or run into any issues, don't hesitate to reach out to your mentor or any team member. Happy coding!</p>`,S=(()=>{let e=class e extends o{constructor(){super(),this.routePrefix="",this.pageType="guide",this.pageContent=w,this.page=s,this.demoAssets=m}};e.\u0275fac=function(l){return new(l||e)},e.\u0275cmp=t({type:e,selectors:[["ng-doc-page-onboarding-process-extension-onboarding"]],standalone:!0,features:[c([{provide:o,useExisting:e},u,s.providers??[]]),d,r],decls:1,vars:0,template:function(l,k){l&1&&g(0,"ng-doc-page")},dependencies:[p],encapsulation:2,changeDetection:0});let n=e;return n})(),D=[i(a({},(0,b.isRoute)(s.route)?s.route:{}),{path:"",component:S,title:"Extension Onboarding"})],z=D;export{S as DynamicComponent,z as default};
