import{a as h}from"./chunk-THHCAWET.js";import{a as u}from"./chunk-6FMLIRIY.js";import{Jb as w,L as c,Ma as d,Na as g,ha as o,ra as r,rb as i}from"./chunk-ZQZS5QUD.js";import{a,b as l,g as y}from"./chunk-P2VZOJAX.js";var f=y(w());var b={title:"QE Architecture Overview",mdFile:"./index.md",category:h,order:8},s=b;var p=[];var k={},m=k;var T=`<h1 id="qe-architecture-overview" class="ngde">QE Architecture Overview<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#qe-architecture-overview"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h1><p class="ngde">The purpose of this document is to define the testing strategy for RiskPhysics, which will be developed using the Scrum agile methodology. It describes how testing will be conducted iteratively and incrementally to ensure that the software meets the specified requirements and maintains high quality.</p><h4 id="scope" class="ngde">Scope<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#scope"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><p class="ngde">This document covers the Testing Strategies for all sprints in the software development life cycle of RiskPhysics. It defines the types of tests, entry and exit criteria, testing environments, and the roles and responsibilities within the testing team.</p><h4 id="testing-objectives" class="ngde">Testing Objectives<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#testing-objectives"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Continuous Quality Assurance</strong>: Validate that each product increment meets the specified functional and non-functional requirements.</li><li class="ngde"><strong class="ngde">Early Defect Detection</strong>: Identify and fix defects within each sprint to prevent the accumulation of issues.</li><li class="ngde"><strong class="ngde">Adaptability</strong>: Adjust testing according to changes in requirements and priorities of each sprint.</li><li class="ngde"><strong class="ngde">Continuous Improvement</strong>: Provide rapid and continuous feedback to improve development and testing processes in each sprint.</li></ul><h4 id="testing-scope" class="ngde">Testing Scope<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#testing-scope"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Areas to be Tested</strong>:<ul class="ngde"><li class="ngde">During each sprint, testing will focus on the User Stories and tasks defined in the Sprint Backlog.</li><li class="ngde">A review of existing functionalities (regression testing) will be conducted to ensure that new developments do not affect current features.</li></ul></li><li class="ngde"><strong class="ngde">Areas Out of Scope</strong>:<ul class="ngde"><li class="ngde">TBD</li></ul></li></ul><h4 id="testing-approach" class="ngde">Testing Approach<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#testing-approach"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Types of Testing</strong><ul class="ngde"><li class="ngde"><strong class="ngde">Unit Testing</strong>: Developers will write and execute unit tests for each new user story, work item or task.</li><li class="ngde"><strong class="ngde">Integration Testing</strong>: Conducted to ensure that components developed in different sprints work correctly together. To achieve this goal we need:<ul class="ngde"><li class="ngde">Identify the components.</li><li class="ngde">Establish the integration criteria.</li><li class="ngde">Define which integrations will be tested.</li><li class="ngde">Include the tests in the sprint planning (In the first moment at least the identification of test cases and manual execution).</li><li class="ngde">Define which Test Cases will be automated.</li><li class="ngde">Automating tests to ensure they run consistently and include a tag to classify the test.</li><li class="ngde">Include a tag in the pull request that specifies the type of test based on the change made.</li><li class="ngde">Configure scripts to run integration tests automatically after build using CI/CD tools (include a tag in the pull request that specifies the type of test and also include in test ). They will be executed every time a tag with the integration type is included in a pull request and this one is completed.</li></ul></li><li class="ngde"><strong class="ngde">System Testing</strong>: Validate that the complete system, including new features, meets the specified requirements. To achieve this goal we need:<ul class="ngde"><li class="ngde">Identify System Test Cases for functional and non-functional test</li><li class="ngde">Ensure the environment is as similar to production as possible to obtain accurate results.</li><li class="ngde">Prepare the test data required to execute the system test cases.</li><li class="ngde">Automating tests to ensure they run consistently.</li><li class="ngde">Configure scripts to run integration tests automatically after merging dev to beta or beta to prod branches using CI/CD tools.</li></ul></li><li class="ngde"><strong class="ngde">Regression Testing</strong>: Automated or manual regression tests will be executed to verify that changes do not introduce new defects. To achieve this goal we need:<ul class="ngde"><li class="ngde">Identify Regression Test Cases</li><li class="ngde">Define which of these regressions will be tested</li><li class="ngde">Include the tests in the sprint planning (In the first moment at least the manual execution).</li><li class="ngde">Define which Test Cases will be automated.</li><li class="ngde">Automating tests to ensure they run consistently and include a tag to classify the test.</li><li class="ngde">Configure scripts to run integration tests automatically after build using CI/CD tools.</li><li class="ngde">Review and adjust regression testing strategy based on results and feedback.</li><li class="ngde">Configure scripts to run integration tests automatically after merging local to dev branches using CI/CD tools.</li></ul></li><li class="ngde"><strong class="ngde">Exploratory Testing</strong>: Performed to discover unforeseen defects.</li></ul></li></ul><h4 id="entry-and-exit-criteria" class="ngde">Entry and Exit Criteria<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#entry-and-exit-criteria"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><p class="ngde"><img src="assets/img/QE_Process.png" alt="QE Process" class="ngde"></p><ul class="ngde"><li class="ngde"><strong class="ngde">Entry Criteria</strong><ul class="ngde"><li class="ngde">Detailed and approved user stories.</li><li class="ngde">Testing environment set up and accessible.</li><li class="ngde">Test cases designed for the sprint's user stories.</li><li class="ngde">Test data prepared and available.</li></ul></li><li class="ngde"><strong class="ngde">Exit Criteria</strong><ul class="ngde"><li class="ngde">All user stories of the sprint have been tested and meet the acceptance criteria.</li><li class="ngde">No critical defects remain open related to the sprint functionalities.</li><li class="ngde">Critical regression tests have passed.</li><li class="ngde">Positive feedback from sprint reviews.</li></ul></li></ul><h4 id="testing-environment" class="ngde">Testing Environment<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#testing-environment"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Hardware and Software Requirements</strong><ul class="ngde"><li class="ngde">Dev environment.</li><li class="ngde">Beta environment that mirrors production.</li><li class="ngde">Production environment.</li><li class="ngde">Configurations adaptable to test specific sprint functionalities.</li><li class="ngde">Kubernetes for running automated tests.</li></ul></li><li class="ngde"><strong class="ngde">Test Data</strong><ul class="ngde"><li class="ngde">Test data will be updated in each sprint to reflect new testing scenarios based on user stories.</li></ul></li><li class="ngde"><strong class="ngde">Testing Tools</strong><ul class="ngde"><li class="ngde">Test management tool: Azure Devops.</li></ul></li></ul><h4 id="test-automation" class="ngde">Test Automation<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#test-automation"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Automation Strategy</strong><ul class="ngde"><li class="ngde">Regression and critical acceptance tests will be automated to enable rapid execution in each sprint.</li><li class="ngde">Unit tests will be automated by developers for all new functionalities.</li></ul></li><li class="ngde"><strong class="ngde">Automation Tools</strong><ul class="ngde"><li class="ngde">Check <a href="/architecture-documentation/architectural-decision-records/adrs-list#test-automation-framework" class="ngde">Test Automation Framework ADR</a></li><li class="ngde"><strong class="ngde">Handling test execution in the cloud</strong>:<table class="ngde"><thead class="ngde"><tr class="ngde"><th class="ngde">VMs</th><th class="ngde">Kubernetes</th></tr></thead><tbody class="ngde"><tr class="ngde"><td class="ngde">Tests can be executed on VMs by installing the Playwright library and running tests similarly to a local environment.</td><td class="ngde">Tests can be executed in Kubernetes by deploying Playwright containers. Kubernetes can manage scaling and distribution of test executions using Playwright's built-in support for running tests in parallel.</td></tr><tr class="ngde"><td class="ngde">-<strong class="ngde">Automation Scope</strong></td><td class="ngde"></td></tr></tbody></table></li><li class="ngde">In each sprint, automated tests will be added for new functionalities and for regression coverage</li></ul></li></ul><h4 id="defect-management" class="ngde">Defect Management<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#defect-management"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Defect Management Process</strong></p><ul class="ngde"><li class="ngde"><strong class="ngde">Detection</strong>: Defects found during the sprint will be logged immediately in Azure DevOps.</li><li class="ngde"><strong class="ngde">Classification and Prioritization</strong>: Defects will be classified and prioritized in the daily Scrum meeting.</li><li class="ngde"><strong class="ngde">Resolution</strong>: Developers will work on fixing defects as part of the sprint work.</li><li class="ngde"><strong class="ngde">Verification</strong>: Testers will verify the fix and close the defect if it is adequately resolved.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Defect Tracking Tools</strong></p><ul class="ngde"><li class="ngde">Azure DevOps</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Planning and Scheduling</strong></p><ul class="ngde"><li class="ngde"><strong class="ngde">Testing Schedule</strong><ul class="ngde"><li class="ngde"><strong class="ngde">Sprint Testing</strong>: Will be conducted during each sprint, with daily reviews and immediate feedback.</li><li class="ngde"><strong class="ngde">Release Testing</strong>: Will be conducted at the end of each release, integrating tests from all sprints.</li></ul></li><li class="ngde"><strong class="ngde">Effort Estimation</strong><ul class="ngde"><li class="ngde">Testing efforts will be estimated during sprint planning for each user story and task.</li></ul></li></ul></li></ul><h4 id="impediments" class="ngde">Impediments<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#impediments"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><p class="ngde">To manage and address impediments effectively by utilizing Azure DevOps work items. This approach helps in promptly identifying, tracking, and resolving issues that block or delay progress within the project.</p><h4 id="test-metrics-and-reporting" class="ngde">Test Metrics and Reporting<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#test-metrics-and-reporting"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Key Metrics</strong></p><ul class="ngde"><li class="ngde">Number of user stories completed and tested in each sprint.</li><li class="ngde">Number of defects found and fixed in each sprint.</li><li class="ngde">Average time to fix defects in each sprint.</li><li class="ngde">Automated test coverage.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Reports</strong></p><ul class="ngde"><li class="ngde">Daily test status report during the sprint.</li><li class="ngde">Report at the end of each sprint and at the end of each release.</li></ul></li></ul><h4 id="test-closure" class="ngde">Test Closure<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#test-closure"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><p class="ngde"><strong class="ngde">Closure Activities</strong></p><ul class="ngde"><li class="ngde">Test feedback at the end of each sprint.</li><li class="ngde">Preparation of the final test report at the end of each release.</li></ul></li><li class="ngde"><p class="ngde"><strong class="ngde">Lessons Learned</strong></p><ul class="ngde"><li class="ngde">Document lessons learned during each sprint retrospective to improve in future sprints.</li></ul></li></ul><h4 id="appendices" class="ngde">Appendices<a title="Link to heading" class="ng-doc-header-link ngde" href="/architecture-documentation/qe-architecture-overview#appendices"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h4><ul class="ngde"><li class="ngde"><strong class="ngde">Supporting Documentation</strong><br class="ngde">[Include links or references to any additional documentation, such as testing policies, test case templates, etc.]</li></ul>`,D=(()=>{let e=class e extends i{constructor(){super(),this.routePrefix="",this.pageType="guide",this.pageContent=T,this.page=s,this.demoAssets=m}};e.\u0275fac=function(n){return new(n||e)},e.\u0275cmp=c({type:e,selectors:[["ng-doc-page-architecture-documentation-qe-architecture-overview"]],standalone:!0,features:[d([{provide:i,useExisting:e},p,s.providers??[]]),o,g],decls:1,vars:0,template:function(n,C){n&1&&r(0,"ng-doc-page")},dependencies:[u],encapsulation:2,changeDetection:0});let t=e;return t})(),A=[l(a({},(0,f.isRoute)(s.route)?s.route:{}),{path:"",component:D,title:"QE Architecture Overview"})],N=A;export{D as DynamicComponent,N as default};
